</div><div class="p-4 overflow-y-auto"><code class="!whitespace-pre hljs language-coffeescript">class Memo
  constructor: ()-&gt;
    @MM = &lcub}

  saveThis: (key, value)-&gt;
    isNewKey = !@MM[key]
    oldResolver = @MM[key]?.resolver ? null
    @MM[key] = &lcub value: value, notifier: new Promise((resolve) =&gt; resolve(value)) }
    @MM[key].resolver = @MM[key].notifier.then
    oldResolver(value) if oldResolver and isNewKey
    for k, v of @MM
      if k instanceof RegExp and k.test(key)
        v.resolver(value) if v.resolver
    @MM[key]

  waitFor: (keys, callback)-&gt;
    unfound = []
    for key in keys
      if @MM[key]? and @MM[key].value?
        continue
      else if @MM[key]?
        unfound.push @MM[key].notifier
      else
        @MM[key] = &lcub notifier: new Promise((resolve) =&gt; @MM[key].resolver = resolve) }
        unfound.push @MM[key].notifier
    Promise.all(unfound).then(callback)

# Usage Example
